import React from 'react'
import {getOrdersDetails} from '../../actions/orderAction'
import { connect } from 'react-router-dom'

class OrdersDisplay extends React.Component{
    constructor(){
        super()
        this.state={             
            path : window.location.origin 
        }
    }
    componentDidMount =()=>{
        this.props.dispatch(getOrdersDetails()) 
        const refersh =  setInterval( () =>{  
            if(this.props.orders.length ) {             
                clearInterval(refersh)   
            }
        } , 1000)
    }
    render(){
        return(
            <div className ='container-fluid m-3 '>
                 <h4 style ={{ visibility : this.props.cartData.length ? 'visible' : 'hidden'}}  > My Cart</h4> 
                {
                   this.props.cartData.length ? 
                   <div className="row justify-content-between">                        
                        <div className="col-6"> 
                        {
                            this.props.cartData.map( (cart,i) =>{
                                return (
                                    <div className="card mb-2"  key ={i+1}>
                                        <div className="row no-gutters">
                                            <div className="col-md-1 mt-3">
                                                <img src={`${this.state.path}/${cart.image}`} className="card-img" alt="No Image" 
                                                /> 
                                            </div>
                                            
                                            <div className="col-md-10">
                                                <div className="card-body">
                                                    <div className='row'>
                                                        <div className ='col'>  
                                                            <h5 className="card-title">{cart.name}</h5>
                                                        </div>
                                                    </div>
                                                    <div>
                                                        <h5 className="card-text text-left mt-3">
                                                            <NumberFormat thousandSeparator={true} thousandsGroupStyle="lakh" 
                                                                className="card-title text-left"
                                                                displayType = 'text' prefix={'â‚¹'} value={cart.price }/>
                                                        </h5> 
                                                    </div> 
                                                </div>
                                                 
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                            
                        } 
                        </div>
                    </div>
                    :''
                }
            </div>
        )
    }
}

const mapStateToProps = (state) =>{
    return{
        orders : state.orders
    }
}

export default connect(mapStateToProps)(OrdersDisplay)
