import React from 'react'
import {connect} from 'react-redux'
import {getProducts} from'../../actions/productAction' 
 

const styles = theme => ({
	root: {
		flexGrow: 1
	},
	paper: {
		padding: theme.spacing.unit * 2,
		margin: "auto",
		maxWidth: 1300,
		maxHeight: "auto"
	},
	image: {
		width: 240,
		height: 220
	},
	img: {
		margin: "auto",
		display: "block",
		maxWidth: "100%",
		maxHeight: "100%"
	}
});

class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            imageSrc:'',
            filefolder:'../../upload/'
        }
    }
    componentDidMount () {
        if (this.props.products.length === 0) {
            this.props.dispatch(getProducts())           
        }

        const refersh =  setInterval( () =>{  
            if(this.props.products.length ) {             
                clearInterval(refersh)
                  
                const imageSrc =   this.props.products[0].mainImage
                console.log(imageSrc,'pro')
                this.setState({imageSrc})
            }
        } , 1000)

    }

    render() { 
         return(
             <div>
                 <h2> Image Link - {this.state.imageSrc.filename}</h2>
                 {
                     this.state.imageSrc != '' ? <img src = { this.state.imageSrc} className ='image img' alt="not found" style ={{width:'240px'}}/> : 'Image not found'
                 }
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
