import React from 'react'
import {connect} from 'react-redux'
import {getProducts} from'../../actions/productAction'
// import file from '../../../../server/media/'

class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            imageSrc:''
        }
    }
    componentDidMount () {
        if (this.props.products.length === 0) {
            this.props.dispatch(getProducts())
            this.setState({imageSrc : `/media/${this.props.products.files}`})
        }

    }

    render() {
        console.log(this.props.products,'Single Product info')
        console.log(this.props.products.mainImage,'Fileinfo')

         return(
             <div>
                 <h2> Image Link - {this.props.products.description}</h2>
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
