import React from 'react'
import {connect} from 'react-redux'
import {getProducts} from'../../actions/productAction' 
  
class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            products:'',
            cartimage:''             
        }
    }
    componentDidMount () {
        if (this.props.products.length === 0) {
            this.props.dispatch(getProducts())           
        }

        const refersh =  setInterval( () =>{  
            if(this.props.products.length ) {             
                clearInterval(refersh)
                  
                const products =   this.props.products[0]
                const cartimage =   this.props.products[0].cartImage
                console.log(this.props.products[0].description.split('--'),'pro')
                this.setState({products,cartimage})
            }
        } , 1000)

    }

    render() { 
         return(
             <div>
                 {  
                     this.state.products != {} ?
                        <div className ='container-fluid ml-2'> 
                            <div className ='row'>                               
                                <div className ='col-4 align-self-center'> 
                                    <img src = {this.state.products.mainImage} className ='image img' alt="not found" style ={{height:'500px'}}/>
                                    <input type= 'submit' id='submit' name ='submit' className ='btn btn-warning w-50' 
                                    value ='ADD TO CART'  /> &ensp;
                                    <input type= 'submit' id='submit' name ='submit' className ='btn btn-warning w-50' 
                                    value ='ADD TO CART'  />
                                </div>

                                <div className ='col'>
                                    <h3>{this.state.products.name}</h3> <h6>&nbsp;</h6>
                                    <h2> <b className ='fa fa-rupee'>{this.state.products.price}</b></h2><h6>&nbsp;</h6>
                                    <h4>
                                        {
                                            this.state.products.description ?
                                            this.state.products.description.split('--').map(text =>{
                                                return(<h4>{text}</h4>)
                                            }) :''
                                     }
                                     </h4>
                                </div>
                            </div>
                         </div>
                     :''
                 }
                 
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
