import React from 'react'
import {connect} from 'react-redux'
import {getProductsbyID} from'../../actions/productAction' 
  
class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            products: {}         
        }
    }
    componentDidMount () {  
        const refresh =() =>{
            return window.location.reload()
        }       
        this.props.dispatch(getProductsbyID(this.props.match.params.id))  

        const refersh =  setInterval( () =>{  
            if(this.props.products.length ) {             
                clearInterval(refersh)                  
                const products =   this.props.products[0] 
                this.setState({products})
            }
        } , 1000)

    }

    render() { 
        
         return(
             <div>
                 { 
                    this.state.products !== {} ?
                        <div className ='container-fluid ml-4 mt-5'> 
                            <div className ='row'>                               
                                <div className ='col-3 align-self-center'> 
                                    <img src = {`${window.location.origin}/${this.state.products.mainImage}`} 
                                          className ='image img mb-5'
                                        alt="Image not found" style = {{ height:'300px',marginLeft: '15%',  marginRight: 'auto'}} />
                                    <br/>
                                    <input type= 'submit' id='submit' name ='submit' className ='btn btn-warning w-40 ml-3' 
                                        value ='ADD TO CART'  />  
                                    <input type= 'submit' id='submit' name ='submit' className ='btn btn-warning w-40 ml-3' 
                                        value ='BUY NOW'  />
                                </div>

                                <div className ='col'>
                                    <h2>{this.state.products.name}</h2> <h6>&nbsp;</h6>
                                    <h4> <span className ='fa fa-rupee'></span> {this.state.products.price}</h4> 
                                        <h6>&nbsp;</h6>
                                    <h4>
                                        {
                                            this.state.products.description ?
                                            this.state.products.description.split('--').map((text,i) =>{
                                                return   <h4 key ={i}>{text}</h4>  
                                            }) : ''
                                     }
                                     </h4>
                                </div>
                            </div>

                         </div>
                     :''
                 }
                 <br/>
                 <div className ='container ml-5'>
                     <h4> Cutomer Reviews:</h4>
                 </div>
                 
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
