import React from 'react'
import {connect} from 'react-redux'
import {getProducts} from'../../actions/productAction' 

class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            imageSrc:'',
            filefolder:'../../../../server/'
        }
    }
    componentDidMount () {
        if (this.props.products.length === 0) {
            this.props.dispatch(getProducts())
           
        }

        const refersh =  setInterval( () =>{  
            if(this.props.products.length ) {             
                clearInterval(refersh)
                const imageSrc = require(this.props.products[0].mainImage.replace('..\client',''))
                console.log(imageSrc,'pro')
                this.setState({imageSrc})
            }
        } , 1000)

    }

    render() { 
         return(
             <div>
                 <h2> Image Link - {this.state.imageSrc}</h2>
                 {
                     this.state.imageSrc ? <img src = {this.state.imageSrc} alt='' /> : 'Image not found'
                 }
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
