import React from 'react' 
import Card from 'react-bootstrap/Card' 
import {Link} from 'react-router-dom' 
import {getProductsbyCategory} from '../../actions/productAction'
import {connect} from 'react-redux'


class  ProductShowByCategory extends React.Component {
   
    constructor(){
        super()
        this.state={
            products :[]
        }
    }
    componentDidMount = ()=>{
        this.props.dispatch(getProductsbyCategory(this.props.match.params.id)) 

        const refersh =  setInterval( () =>{            
            if(this.props.products.length){
                clearInterval(refersh) 
                this.setState({products:this.props.products})
            }
        },1000)
 
    }
 
    render() {
            console.log()
        return( 
        
            <div>
              {
                  this.state.products.length   ?
                  <div className ='container-fluid mt-3 ml-2'>  
                        {
                            this.state.products.map((product,i) =>{   
                                console.log(product.mainImage)                              
                                    return (
                                        <Card className = 'border-none' bg='light' style ={{height:'100%'}} >
                                                <Card.Img variant="top" src= {product.mainImage}
                                                    style ={{height:'200px',width:'50%',marginLeft: 'auto',  marginRight: 'auto'}}/>
                                                <Card.Body>
                                                    <Card.Title>{product.name}</Card.Title>
                                                    <Card.Text>
                                                        <span className=" fa fa-rupee" id="basic-addon1"></span> {product.price}
                                                    </Card.Text>
                                                    
                                                </Card.Body>
                                                </Card>
                                    )
                            })
                        }
                     
                     
                  </div> 
                  : <h2> Products not Retrived</h2>
              }
 
            </div>
               
        )
    }
}

const mapStateToProps = (state) =>{
    return { 
        products:state.products
    }
}
export default connect(mapStateToProps)(ProductShowByCategory)
