import React from 'react'
import {connect} from 'react-redux'
import {getProducts} from'../../actions/productAction' 
 

const styles = theme => ({
	root: {
		flexGrow: 1
	},
	paper: {
		padding: theme.spacing.unit * 2,
		margin: "auto",
		maxWidth: 1300,
		maxHeight: "auto"
	},
	image: {
		width: 240,
		height: 220
	},
	img: {
		margin: "auto",
		display: "block",
		maxWidth: "100%",
		maxHeight: "100%"
	}
});

class ShowSingleProduct extends React.Component{

    constructor () {
        super()
        this.state = {
            imageSrc:'',
            cartimage:'',
            filefolder:'../../upload/'
        }
    }
    componentDidMount () {
        if (this.props.products.length === 0) {
            this.props.dispatch(getProducts())           
        }

        const refersh =  setInterval( () =>{  
            if(this.props.products.length ) {             
                clearInterval(refersh)
                  
                const imageSrc =   this.props.products[0].mainImage
                const cartimage =   this.props.products[0].cartImage
                console.log(this.props.products[0].name,'pro')
                this.setState({imageSrc,cartimage})
            }
        } , 1000)

    }

    render() { 
         return(
             <div>
                 {
                     this.props.products[0] !== {} ?
                        <div className ='container'>
                            <div className ='row'>
                                <h3>{this.props.products.name}</h3>
                                <div className ='col-3 border'> 
                                <img src = {this.state.imageSrc} className ='image img' alt="not found" style ={{height:'400px'}}/>
                                </div>
                            </div>
                         </div>
                     :''
                 }
                 
             </div>
         )
    }
}

const mapStateToProps =(state)=>{
    return {
        products:state.products
    }
}

export default connect(mapStateToProps)(ShowSingleProduct)
