import React from 'react'
import {connect} from 'react-redux'
import {getCategories} from '../../actions/categoryAction'


class CategoryUpdate extends React.Component {

    constructor(){
        super()
        this.state = {
            categoryType :''
        }
    }
    
    componentDidMount = ()=>{
        this.props.dispatch(getCategories()) 

        const refersh =  setInterval( () =>{ 
            if(this.props.categories.length ) {             
                clearInterval(refersh)             
            }
        },1000)
    }

    render(){
        return(
            <div className ='container'>
                <h3> Add / Update Category </h3>
                <form className ='px-4 py-3'>
                    <div className="form-group">
                    <h6 className ='mt-3'>Category Type</h6>
                    <div className ='row ml-2'>                    
                        <div className="form-check form-check-inline">
                            <input className="form-check-input" type="radio" name="gender" 
                                id="Male" value="Male" disabled ={this.state.infoEdit} onChange ={this.handleChange}
                                checked = {this.state.gender ==='Male' ? true : false} />
                            <label className="form-check-label" htmlFor="Male">Existing Category</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input className="form-check-input" type="radio" name="gender" 
                                id="Female" value="Female" disabled ={this.state.infoEdit} onChange ={this.handleChange} 
                                checked = {this.state.gender ==='Female' ? true : false} />
                            <label className="form-check-label" htmlFor="Female">New Category</label>
                        </div>
                    </div>
                    </div>
                </form>
            </div>
        )
    }
}



const mapStateToProps =(state) =>{
    return {
        categories :state.categories
    }
}

export default connect(mapStateToProps)(CategoryUpdate)
