import React from 'react'
import logo from '../../media/logo3.PNG'


class Login extends React.Component {
    constructor() {
        super();
        this.state = {
            email: '',
            password :''
        }
    }


    render() {
        return(
            <div className = 'container'>
                <div className ='row justify-content-center mt-5 border'>
                    <div className ='col-sm-5'> 
                        <form className = 'form'>                                                    
                            <img src = {logo} className = 'img-thumbnail rounded  mx-auto d-block mb-4'/>
                            <div className = 'form-group'>
                                <label htmlFor='email' >Email :</label>
                                <input type= 'text'id='email' name ='email' className ='form-control' placeholder ='Enter the Email' />
                            </div>
                            <div className = 'form-group'>
                                <label htmlFor='password' >Password :</label>
                                <input type= 'text'id='email' name ='email' className ='form-control' placeholder ='Enter the Email' />
                            </div>
                            
                        </form> 
                    </div>
                </div>


            </div>
        )
    }
}

export default Login
